INSERT INTO MEDIA (ID, LABEL, CATEGORY, TYPE, AUTHOR_NAME, AUTHOR_SURNAME, DESCRIPTION, MEDIA_IMAGE_URL, PUBLICATION_DATE)
VALUES ('4', 'Le seigneur des anneaux','LIVRE', 'FANTASY', 'Tolkien','J.R.R','Prenant place dans le monde de fiction de la Terre du Milieu, il suit la quête du hobbit Frodo Bessac, qui doit détruire l''Anneau unique afin que celui-ci ne tombe pas entre les mains de Sauron, le Seigneur des ténèbres. Plusieurs personnages lui viennent en aide, parmi lesquels son serviteur Sam, le mage Gandalf ou encore l''humain Aragorn, héritier d''une longue lignée de rois. ','https://images.fr.shopping.rakuten.com/photo/1270487382.jpg','1954-07-29');

INSERT INTO MEDIA (ID, LABEL, CATEGORY, TYPE, AUTHOR_NAME, AUTHOR_SURNAME, DESCRIPTION, MEDIA_IMAGE_URL, PUBLICATION_DATE)
VALUES ('5', 'Star Wars Episode 1 - La menace fantôme', 'FILM', 'SF', 'Lucas','George', 'Avant de devenir un célèbre chevalier Jedi, et bien avant de se révéler l’âme la plus noire de la galaxie, Anakin Skywalker est un jeune esclave sur la planète Tatooine. La Force est déjà puissante en lui et il est un remarquable pilote de Podracer. Le maître Jedi Qui-Gon Jinn le découvre et entrevoit alors son immense potentiel. Pendant ce temps, l’armée de droïdes de l’insatiable Fédération du Commerce a envahi Naboo, une planète pacifique, dans le cadre d’un plan secret des Sith visant à accroître leur pouvoir. Pour défendre la reine de Naboo, Amidala, les chevaliers Jedi vont devoir affronter le redoutable Seigneur Sith, Dark Maul. ', 'https://www.cinemaffiche.fr/4015-tm_large_default/star-wars-la-menace-fantome-episode-1-star-wars-1-the-phantom-menace.jpg', '1999-10-13');

INSERT INTO MEDIA (ID, LABEL, CATEGORY, TYPE, AUTHOR_NAME, AUTHOR_SURNAME, DESCRIPTION, MEDIA_IMAGE_URL, PUBLICATION_DATE)
VALUES ('6', 'Equinoxe', 'MUSIQUE', 'ELECTRO', 'Jarre','Jean-Michel','L''album est enregistré dans le studio privé de Jean-Michel Jarre sur l''équipement MCI-Studio et mixé aux Gang Recording Studio à Paris, entre janvier et août 1978. Jean-Michel Jarre déclare à propos d''Équinoxe : « J''ai voulu évoquer l''écoulement des 24 heures d''une journée, chaque partie de l''album représentant un moment différent du jour et de la nuit. Je veux que les gens se servent de mon disque pour le passer à différents moments de la journée et quand ils sont d''humeurs différentes ». Dans cet album, le musicien emploie beaucoup plus d''éléments rythmiques et dynamiques, en particulier sur l''utilisation du séquençage des basses, notamment grâce à des équipements spécialement fabriqués par son ami et collaborateur Michel Geiss. ','https://p1.storage.canalblog.com/29/39/636073/52226885.jpg','1978-11-17');

