INSERT INTO MEDIA (ID, LABEL, CATEGORY, TYPE, AUTHOR_NAME, AUTHOR_SURNAME, DESCRIPTION, MEDIA_IMAGE_URL, PUBLICATION_DATE)
VALUES ('20', 'Les neuf princes d''Ambre','LIVRE', 'FANTASY', 'Zelazny','Roger','Ambre est un royaume médiéval ; comme tous les autres il possède une capitale, un palais, une cour, une famille royale et des intrigues. Mais Ambre est aussi bien plus : lieu mythique considéré comme le centre de l''univers. Seul lieu réel, tous les autres n''en sont qu''un reflet, dans l''ombre, comme la Terre. Considérée comme très intéressante par la plupart des princes d''Ambre, elle leur sert de résidence secondaire, voire de cachette. Car la vie d''un prince ou d''une princesse n''est pas de tout repos. Seuls dépositaires du pouvoir quasi divin de se déplacer entre les mondes, ils n''en restent pas moins humains : au mieux rivaux, ils sont le plus souvent ennemis jurés. En effet, depuis la disparition mystérieuse d''Obéron, le père de cette grande famille, le trône est vacant et la succession des plus agitée.','https://images-na.ssl-images-amazon.com/images/I/4164Zob5KgL.jpg','1970-06-01');

INSERT INTO MEDIA (ID, LABEL, CATEGORY, TYPE, AUTHOR_NAME, AUTHOR_SURNAME, DESCRIPTION, MEDIA_IMAGE_URL, PUBLICATION_DATE)
VALUES ('21', 'La reine des neiges', 'FILM', 'ENFANT', 'Buck','Chris', 'Anna, une jeune fille aussi audacieuse qu’optimiste, se lance dans un incroyable voyage en compagnie de Kristoff, un montagnard expérimenté, et de son fidèle renne, Sven à la recherche de sa sœur, Elsa, la Reine des Neiges qui a plongé le royaume d’Arendelle dans un hiver éternel…  En chemin, ils vont rencontrer de mystérieux trolls et un drôle de bonhomme de neige nommé Olaf, braver les conditions extrêmes des sommets escarpés et glacés, et affronter la magie qui les guette à chaque pas. ', 'http://img.over-blog-kiwi.com/0/55/25/40/20140226/ob_c43127_1011006-fr-la-reine-des-neiges-138366440.jpg', '2013-12-04');

